import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})

export class AppComponent {

  appName:string = 'fundamentals-app';
  angularLogo:string = "https://angular.io/assets/images/logos/angular/angular.svg"

}
<div class="container">
  <h1>
    <img [src]="angularLogo" width="75" height="75" />
    Fundamentos Angular
  </h1>

  <!-- #region INTERPOLACIÓN -->
  <h2># Interpolación</h2>
  <div class="interpolacion">
    <span>AppName: {{ appName }}</span>
    <span> {{ 'hi angular' }} </span>
  </div>
  <!-- #endregion -->
</div>
